import { Link } from '../routes';
import { slug } from '../helpers/slug';
export const PodcastsList = ({ audioClips }) => (
    <>
        {audioClips.map((clip) => (
            <Link
                oncClick={() => console.log('Hola')}
                key={clip.id}
                route="podcast"
                params={{
                    slug: `${slug(clip.channel.title)}/${slug(clip.title)}`,
                    id: clip.id,
                    idChannel: clip.channel.id,
                }}>
                <a className="podcast">
                    <h3>{clip.title}</h3>
                    <div className="meta">
                        {Math.ceil(clip.duration / 60)} minutes
                    </div>
                </a>
            </Link>
        ))}

        <style jsx>
            {`
                .podcast {
                    display: block;
                    text-decoration: none;
                    color: #333;
                    padding: 15px;
                    border-bottom: 1px solid rgba(0, 0, 0, 0.2);
                    cursor: pointer;
                }
                .podcast:hover {
                    color: #000;
                }
                .podcast h3 {
                    margin: 0;
                }
                .podcast .meta {
                    color: #666;
                    margin-top: 0.5em;
                    font-size: 0.8em;
                }
            `}
        </style>
    </>
);
