const Channel = ({ channel, audioClips = [], series = [] }) => {
    return (
        <>
            <header>Podcast</header>
            <h1>{channel.title}</h1>

            {audioClips.map((audio) => (
                <div>{audio.title}</div>
            ))}
            {serie.map((audio) => (
                <div>{audio.title}</div>
            ))}

            <style jsx>{`
                h1 {
                    font-weight: 700;
                    padding: 15px;
                }
                header {
                    color: #fff;
                    background: #8756ca;
                    padding: 15px;
                    text-align: center;
                }
                .channels {
                    display: grid;
                    grid-gap: 15px;
                    padding: 15px;
                    grid-template-columns: repeat(
                        auto-fill,
                        minmax(160px, 1fr)
                    );
                }
                a.channel {
                    display: block;
                    margin-bottom: 0.5em;
                    color: #333;
                    text-decoration: none;
                }
                .channel img {
                    border-radius: 3px;
                    box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
                    width: 100%;
                }
                h2 {
                    padding: 5px;
                    font-size: 0.9em;
                    font-weight: 600;
                    margin: 0;
                    text-align: center;
                }
            `}</style>

            <style jsx global>{`
                body {
                    margin: 0;
                    font-family: system-ui;
                    background: white;
                }
            `}</style>
        </>
    );
};

export async function getServerSideProps({ query }) {
    const idChannel = query.id;
    const reqChannel = await fetch(
        `https://api.audioboom.com/channels/${idChannel}`
    );
    const dataChanel = await reqChannel.json();
    const channel = dataChanel.body.channel;

    const reqAudio = await fetch(
        `https://api.audioboom.com/channels/${idChannel}/audio_clips`
    );
    const dataAudio = await reqAudio.json();
    const audioClips = dataAudio.body.audio_clips;

    const reqSeries = await fetch(
        `https://api.audioboom.com/channels/${idChannel}/child_channels`
    );
    const dataSeries = await reqSeries.json();
    const series = dataSeries.body.channels;

    return { props: { channel, audioClips, series } };
}

export default Channel;
