import React from 'react';
import Link from 'next/link';
import { Layout } from '../components/Layout';
import { ReturnModal } from '../components/ReturnModal';
import { Player } from '../components/Player';

export default function Podcast({ podcast = {} }) {
    return (
        <Layout title={podcast.title}>
            <ReturnModal id={podcast.channel.id}>
                <picture>
                    <div
                        style={{
                            backgroundImage: `url(${
                                podcast.urls.image ||
                                podcast.channel.urls.logo_image.original
                            })`,
                        }}
                    />
                </picture>

                <Player
                    title={podcast.title}
                    userName={podcast.channel.title}
                    audio={podcast.urls.high_mp3}
                />
            </ReturnModal>
            <style jsx>{`
                picture {
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    flex: 1 1;
                    flex-direction: column;
                    width: auto;
                    padding: 10%;
                }
                picture div {
                    width: 100%;
                    height: 100%;
                    background-position: 50% 50%;
                    background-size: contain;
                    background-repeat: no-repeat;
                }
            `}</style>
        </Layout>
    );
}

export async function getServerSideProps({ query: { id } }) {
    try {
        const request = await fetch(
            `https://api.audioboom.com/audio_clips/${id}.mp3`
        );
        const data = await request.json();
        const podcast = data.body.audio_clip;
        return { props: { podcast } };
    } catch (err) {
        console.log(err);
    }
}
