import Link from 'next/link';
import Head from 'next/head';
export const Layout = ({ children, title }) => {
    return (
        <>
            <Head>
                <title>{title}</title>
            </Head>
            <header>
                <Link href="/">
                    <a>Podcast</a>
                </Link>
            </header>

            {children}

            <style>
                {`
                header {
                    color: #fff;
                    background: #8756ca;
                    padding: 15px;
                    text-align: center;
                }
                header a{
                    color: #fff;
                    text-decoration:none;
                }`}
            </style>
            <style jsx global>{`
                body {
                    margin: 0;
                    font-family: system-ui;
                    background: white;
                }
            `}</style>
        </>
    );
};
