import { Layout } from '../components/Layout';
import { SeriesLits } from '../components/SeriesList';
import { PodcastsList } from '../components/PodcastsList';
const Channel = ({ channel, audioClips = [], series = [] }) => {
    return (
        <Layout title={`Channel ${channel.title}`}>
            <h1>{channel.title}</h1>

            <h2>Series</h2>
            <SeriesLits series={series} />

            <h2>Ultimos Podcasts</h2>
            <PodcastsList audioClips={audioClips} />

            <style jsx>{`
                .banner {
                    width: 100%;
                    padding-bottom: 25%;
                    background-position: 50% 50%;
                    background-size: cover;
                    background-color: #aaa;
                }

                h1 {
                    font-weight: 600;
                    padding: 15px;
                }
                h2 {
                    padding: 5px;
                    font-size: 32px;
                    font-weight: 600;
                    margin: 0;
                    text-align: center;
                }
            `}</style>

            <style jsx global>{`
                body {
                    margin: 0;
                    font-family: system-ui;
                    background: white;
                }
            `}</style>
        </Layout>
    );
};

export async function getServerSideProps({ query }) {
    const idChannel = query.id;

    const [reqChannel, reqSeries, reqAudio] = await Promise.all([
        fetch(`https://api.audioboom.com/channels/${idChannel}`),
        fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`),
        fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`),
    ]);

    const dataChanel = await reqChannel.json();
    const channel = dataChanel.body.channel;

    const dataAudio = await reqAudio.json();
    const audioClips = dataAudio.body.audio_clips;

    let dataSeries = await reqSeries.json();
    let series = dataSeries.body.channels;

    return { props: { channel, audioClips, series } };
}

export default Channel;
