import Link from 'next/link';
export const PodcastsList = ({ audioClips }) => (
    <>
        <h2>Ultimos Podcasts</h2>
        {audioClips.map((clip) => (
            <Link href={`/podcast?id=${clip.id}`} prefetch key={clip.id}>
                <a className="podcast">
                    <h3>{clip.title}</h3>
                    <div className="meta">
                        {Math.ceil(clip.duration / 60)} minutes
                    </div>
                </a>
            </Link>
        ))}
    </>
);
