import Link from 'next/link';
export const ReturnModal = ({ id }) => (
    <>
        <div className="modal">
            <div className="podcast">
                <nav>
                    <Link href={`/channel?id=${id}`}>
                        <a className="close">&lt; Volver</a>
                    </Link>
                </nav>
            </div>
        </div>
        <style>
            {`
                nav {
                    background: none;
                }
                nav a {
                    display: inline-block;
                    padding: 15px;
                    color: white;
                    cursor: pointer;
                    text-decoration: none;

                }
                
                .modal {
                    position: fixed;
                    top: 0;
                    left: 0;
                    right: 0;
                    bottom: 0;
                    z-index: 99999;
                }
                `}
        </style>
    </>
);
